DROP DATABASE IF EXISTS MYDB;
CREATE DATABASE MYDB;
USE MYDB;
CREATE TABLE SPEAKER ( ID INT PRIMARY KEY NOT NULL auto_increment, PHOTO_LOCATION TEXT ,FIRST_NAME VARCHAR (50),  SURNAME VARCHAR(50), RESARCH_AREA TEXT,JOB_DETAILS TEXT,  LINKED_IN_PROFILE VARCHAR (100));
CREATE TABLE ROOM (ID INT PRIMARY KEY NOT NULL auto_increment,TOTAL_NUMBER_SEATS INT, SEATS_OCCUPIED INT,  MAP_POSE TEXT, ID_CODE TEXT);
CREATE TABLE PRESENTATION (ID_PRESENTATION INT PRIMARY KEY NOT NULL auto_increment,ID_SPEAKER1 INT NOT NULL,  ID_SPEAKER2 INT,ID_SPEAKER3 INT,  ID_ROOM INT NOT NULL,  TITLE VARCHAR(50) NOT NULL UNIQUE, SUMMARY TEXT, START_TIME TIME (0),   END_TIME TIME (0),HASHTAGS TEXT);
ALTER TABLE PRESENTATION ADD FOREIGN KEY (`ID_ROOM`) REFERENCES ROOM (`ID`); 
ALTER TABLE PRESENTATION ADD FOREIGN KEY (`ID_SPEAKER1`) REFERENCES SPEAKER (`ID`); 
ALTER TABLE PRESENTATION ADD FOREIGN KEY (`ID_SPEAKER2`) REFERENCES SPEAKER (`ID`); 
ALTER TABLE PRESENTATION ADD FOREIGN KEY (`ID_SPEAKER3`) REFERENCES SPEAKER (`ID`);